import { useParams } from 'react-router-dom'
import articles from './articlecontent'

const Article = () => {
    const url = useParams()
    const article = articles.find(article => article.slug === url.slug )
    
    return (
    <>
        <div>
            <h1>{article.title}</h1>
            <p>{article.content}</p>
        </div>
    </>
    )
}

export default Article
